	#ifdef ENABLE_HASH
	size_t CommJointDataCore::generateDataHash(const DATATYPE &data)
	{
		size_t seed = 0;
		
		boost::hash_combine(seed, data.isJointTracked);
		seed += DomainHumanTracking::Comm2dVector::generateDataHash(data.jointPosition2D);
		seed += DomainHumanTracking::Comm2dVector::generateDataHash(data.jointVelocity2D);
		seed += DomainHumanTracking::Comm3dVector::generateDataHash(data.jointPosition3d);
		seed += DomainHumanTracking::Comm3dVector::generateDataHash(data.jointVelocity3d);
		
		return seed;
	}
	#endif
	
