	void CommJointDataCore::to_ostream(std::ostream &os) const
	{
	  os << "CommJointData(";
	  getIsJointTracked().to_ostream(os);
	  getJointPosition2D().to_ostream(os);
	  getJointVelocity2D().to_ostream(os);
	  getJointPosition3d().to_ostream(os);
	  getJointVelocity3d().to_ostream(os);
	  os << ") ";
	}
	
	// convert to xml stream
	void CommJointDataCore::to_xml(std::ostream &os, const std::string &indent) const {
		os << indent << "<isJointTracked>";
		getIsJointTracked().to_xml(os, indent);
		os << indent << "</isJointTracked>";
		os << indent << "<jointPosition2D>";
		getJointPosition2D().to_xml(os, indent);
		os << indent << "</jointPosition2D>";
		os << indent << "<jointVelocity2D>";
		getJointVelocity2D().to_xml(os, indent);
		os << indent << "</jointVelocity2D>";
		os << indent << "<jointPosition3d>";
		getJointPosition3d().to_xml(os, indent);
		os << indent << "</jointPosition3d>";
		os << indent << "<jointVelocity3d>";
		getJointVelocity3d().to_xml(os, indent);
		os << indent << "</jointVelocity3d>";
	}
	
	// restore from xml stream
	void CommJointDataCore::from_xml(std::istream &is) {
		static const Smart::KnuthMorrisPratt kmp_isJointTracked("<isJointTracked>");
		static const Smart::KnuthMorrisPratt kmp_jointPosition2D("<jointPosition2D>");
		static const Smart::KnuthMorrisPratt kmp_jointVelocity2D("<jointVelocity2D>");
		static const Smart::KnuthMorrisPratt kmp_jointPosition3d("<jointPosition3d>");
		static const Smart::KnuthMorrisPratt kmp_jointVelocity3d("<jointVelocity3d>");
		
		if(kmp_isJointTracked.search(is)) {
			DomainHumanTracking::JointTrackingStateType isJointTrackedItem;
			isJointTrackedItem.from_xml(is);
			setIsJointTracked(isJointTrackedItem);
		}
		if(kmp_jointPosition2D.search(is)) {
			DomainHumanTracking::Comm2dVector jointPosition2DItem;
			jointPosition2DItem.from_xml(is);
			setJointPosition2D(jointPosition2DItem);
		}
		if(kmp_jointVelocity2D.search(is)) {
			DomainHumanTracking::Comm2dVector jointVelocity2DItem;
			jointVelocity2DItem.from_xml(is);
			setJointVelocity2D(jointVelocity2DItem);
		}
		if(kmp_jointPosition3d.search(is)) {
			DomainHumanTracking::Comm3dVector jointPosition3dItem;
			jointPosition3dItem.from_xml(is);
			setJointPosition3d(jointPosition3dItem);
		}
		if(kmp_jointVelocity3d.search(is)) {
			DomainHumanTracking::Comm3dVector jointVelocity3dItem;
			jointVelocity3dItem.from_xml(is);
			setJointVelocity3d(jointVelocity3dItem);
		}
	}
	
